2. mac下数据库编码配置/etc/my.cnf

[mysqld]
character_set_server=utf8
[client]
default-character-set=utf8

3. 建表语句

DROP DATABASE kxw_test;

CREATE DATABASE kxw_test DEFAULT CHARACTER SET UTF8 ;

SHOW VARIABLES LIKE '%charac%';

USE kxw_test;

DROP TABLE kxw_user;

CREATE TABLE kxw_user(
  id INT(11) PRIMARY KEY AUTO_INCREMENT,
  username VARCHAR(64) NOT NULL DEFAULT '' COMMENT '用户名称',
  password VARCHAR(64) NOT NULL DEFAULT '' COMMENT '用户密码',
  nickname VARCHAR(64) COMMENT '用户昵称'
) ENGINE=InnoDB DEFAULT CHARSET = UTF8;

INSERT INTO kxw_user (username, password, nickname) VALUES ('zhangsan', '123456', '张三');

SELECT * FROM kxw_user;

4. 修改数据库或表编码

ALTER DATABASE kxw_test CHARACTER SET UTF8;

ALTER TABLE kxw_user CHARACTER SET UTF8;


